query GetUser($id: ID!) {
  query: getUser(id: $id) {
    id
    groups
    created_at
    name
    preferred_username
    picture
    settings {
      id
      twitter
      hackerone
      linkedin
      stack_overflow
      bugcrowd
      instagram
      __typename
    }
    tier {
      id
      name
      __typename
    }
    __typename
  }
}
