query fetchValidDisclosures($nextToken: String) {
  query: listValidVulnerabilities(nextToken: $nextToken) {
    nextToken
    items {
      id
      createdAt
      validation_at
      patched_at
      patch_commit_sha
      cwe {
        id
        description
        title
        __typename
      }
      repository {
        id
        owner
        name
        __typename
      }
      disclosure {
        id
        activity {
          id
          user {
            id
            preferred_username
            __typename
          }
          __typename
        }
        __typename
      }
      fix {
        items {
          id
          status
          payout_id
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
