query fetchValidDisclosures($nextToken: String) {
  query: listValidVulnerabilities(nextToken: $nextToken) {
    nextToken
    items {
      id
      createdAt
      validation_at
      patched_at
      patch_commit_sha
      cve_id
      _author {
        id
        preferred_username
        __typename
      }
      cwe {
        id
        description
        title
        __typename
      }
      repository {
        id
        owner
        name
        language
        __typename
      }
      disclosure {
        id
        amount
        activity {
          id
          user {
            id
            preferred_username
            __typename
          }
          __typename
        }
        __typename
      }
      new_permalinks {
        items {
          id
          bounty {
            id
            amount
            __typename
          }
          __typename
        }
        __typename
      }
      fix {
        items {
          id
          status
          payout_id
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
